  .tooltip('name*value', (name, value) => {
    return {
      name: '实际' + name,
      value,
    };
  })
  .style({
    lineWidth: 1,
    stroke: '#fff',
  })
  .animate({
    appear: {
      animation: 'fade-in'
    },
    update: {
      annotation: 'fade-in'
    }
  });

chart.interaction('element-active');

chart.render();

